import React from "react";

function App() {

  const data = [
    {
      question: <img src="../memes/bhopendra jogi.jpg" />,
      answerOptions: [
        {answerText: "Kalia", isCorrect: false},
        {answerText: "Guddu Bhaiya", isCorrect: false},
        {answerText: "Bhupendra Jogi", isCorrect: true},
        {answerText: "Deepak Kalal", isCorrect: false}
      ]
    },
    {
      question: <img src="../memes/phir hera pheri.jpg" />,
      answerOptions: [
        {answerText: "Bohot Maza", isCorrect: false},
        {answerText: "Maza", isCorrect: true},
        {answerText: "Fategi", isCorrect: false},
        {answerText: "Saza", isCorrect: false}
      ]
    },
  ]



  return (
    <div className="App">
      { false ? (<div>Your score is 1 out of 2</div>) : 
         <>
           <div>{data[1].question}</div>
           <div>{data[1].answerOptions.map((answer)=>(
                 <button>{answer.answerText}</button>)
           )}
            </div>
         </>
      }

    </div>
  )
}

export default App;
